module rom_1(
	input clk,
	input rst,
	input [9:0] address,
	output [511:0] data
);
logic [511:0] rf [37:0];

always_ff @ (posedge clk) begin
	if (rst) begin
		rf [0] <= 0;
		rf [1] <= 512'b ;   // A
		rf [2] <= 512'b ;  //B
		rf [3] <= 512'b ;  //C  //Esta mal
		rf [4] <= 512'b ;  //D
		rf [5] <= 512'b ;  //E
		rf [6] <= 512'b ;  //F  
		rf [7] <= 512'b ;  //G  
		rf [8] <= 512'b ; //H
		rf [9] <= 512'b ; //I
		rf [10] <= 512'b ; //J
		rf [11] <= 512'b ; //K
		rf [12] <= 512'b ; //L
		rf [13] <= 512'b ; //M
		rf [14] <= 512'b ; //N
		rf [15] <= 512'b ; //O
		rf [16] <= 512'b ; //P
		rf [17] <= 512'b ; //Q
		rf [18] <= 512'b ; //R
		rf [19] <= 512'b ; //S
		rf [20] <= 512'b ; //T
		rf [21] <= 512'b ; //U
		rf [22] <= 512'b ; //V
		rf [23] <= 512'b ; //W
		rf [24] <= 512'b ; //X //esta mal
		rf [25] <= 512'b ; //Y
		rf [26] <= 512'b ; //z
		rf [27] <= 512'b ; // Â´
